    def on_tab_changed(self, index):

        # current_widget = self.tabWidget.widget(index) # 获取当前索引下的标签也名称
        # index = self.tabWidget.indexOf(self.tab_4)   # 获取标签页得索引
        # 检查切换到的标签页是否是tab_4
        if self.tabWidget.widget(index) == self.tab_4:
            # 弹出密码输入对话框 
            password, ok = QtWidgets.QInputDialog.getText(None, "密码验证", "请输入密码:", QtWidgets.QLineEdit.EchoMode.Password)
            if ok:
                if password == self.password:
                    # 密码正确，允许切换到tab_4
                    self.tab_4_container.setVisible(True)
                else:
                    # 密码错误，显示错误消息并阻止切换
                    QtWidgets.QMessageBox.warning(None, "错误", "密码错误！")
                    # 切换回上一个标签页
                    self.tabWidget.setCurrentIndex(0)
            else:
                # 用户取消输入，阻止切换
                self.tabWidget.setCurrentIndex(0)

